import java.awt.*;import java.time.LocalDate;import java.util.Scanner;public class Main {    static Scanner reader = new Scanner(System.in);    static Owner owner = new Owner();    static Frame frame = new Frame();    static Color mainBg = new Color(0xbdc0bf);    static Color secondBg = new Color(0x42F3EB);    static Color mainBtn = new Color(0x30B2AD);    static Color secondBtn = new Color(0x6E30B2AD, true);    static Color signOut_back = new Color(0xFF0000);    static void signOut() {        App app = new App();        app.Choose(owner);    }    public static void main(String[] args) {        Doctor defaultDoctor = new Doctor();        defaultDoctor.fullName = "yacin";        defaultDoctor.Id = "GM2005268";        defaultDoctor.code = "GM2005268";        owner.doctors.add(defaultDoctor);        Patient defaultPatient = new Patient();        defaultPatient.fullName = "yacin";        defaultPatient.code = "GM2005268";        defaultPatient.patDoc=defaultDoctor;        defaultPatient.appointment = new MedicalHistory(defaultDoctor);        defaultPatient.appointment.date = new Dmy(3,7,2026);        defaultPatient.futureAppointment.add(defaultPatient.appointment);        MedicalHistory p = new MedicalHistory(defaultDoctor);        p.date = new Dmy(3,7,2012);        defaultPatient.futureAppointment.add(p);        owner.patients.add(defaultPatient);        for (Patient patient : owner.patients) {            patient.futureAppointment.removeIf(fA -> {                if (fA.date.getDmy().isBefore(LocalDate.now())) {                    patient.medicalHistories.add(fA);                    return true;                }                return false;            });        }        signOut();     }}